import React, {useState} from 'react'
import '../Assets/Main.css'
import TopBun from '../Assets/Images/topBun.png'
import LowBun from '../Assets/Images/lowBun.png'
// import Tomato from '../Assets/Images/Tomato.png'
// import Chicken from '../Assets/Images/chicken.png'
// import Cheese from '../Assets/Images/Cheese.png'
// import Butter from '../Assets/Images/Butter.png'
// import Lettuce from '../Assets/Images/Lettuce.png'
import BurgerCustomize from './BurgerCustomize'

function Main() {
    const [burgeritems, setburgeritems] = useState([]);
    const [chickenCount, setchickenCount] = useState(0)
    const [tomato, settomato] = useState(0)
    const [cheese, setcheese] = useState(0)
    const [butter, setbutter] = useState(0)
    const [lettuce, setlettuce] = useState(0)
    const createBurger = (path, type) => {
        console.log('fn: createBurger');
        setburgeritems([{
            path: path,
            type: type
        }, ...burgeritems])
        switch(type) {
            case 'Chicken':
                setchickenCount(chickenCount + 1);
                console.log(chickenCount)
                break;
            case 'Tomato':
                settomato(tomato + 1);
                break;
            case 'Cheese':
                setcheese(cheese + 1);
                break;
            case 'Butter':
                setbutter(butter + 1);
                break;
            case 'Lettuce':
                setlettuce(lettuce + 1);
                break;
            default:
                console.log(type)
        }
    }

    return (
        <div>
            <div className='Title'>
                Burger
            </div>
            <div className="Burger">
                <>
                    <img src={TopBun} alt='BurgerTopBun'/>
                    {/* <p>No Items</p> */}
                    {
                        burgeritems.length > 0 && <>{
                            burgeritems.map((item) => {
                                console.log(item)
                                return(
                                    <img src={item.path[item.type]} alt={item.type} />
                                )
                            })
                            }</>
                    }
                    <img src={LowBun} alt='BurgerLowerBun'/>
                </>
            </div>
            <BurgerCustomize Customize={createBurger} chickenC={chickenCount}/>
        </div>

    )
}

export default Main
